import { Component, OnInit } from '@angular/core';
import { FeedbackService } from 'src/app/services/feedback.service';
import { HttpClient } from '@angular/common/http';
import { Router } from '@angular/router';

@Component({
  selector: 'app-feedback',
  templateUrl: './feedback.component.html',
  styleUrls: ['./feedback.component.css']
})
export class FeedbackComponent implements OnInit {

  feedbacks: any[] = [];
  newFeedback: any = {};
  editingFeedbacks: any = null;
  error = '';

  private apiUrl = 'http://localhost:3000/api/feedback';

  constructor(
    private feedbackService: FeedbackService, private http: HttpClient, private router: Router) { }

  ngOnInit(): void {
    this.loadFeedbacks();
  }

  loadFeedbacks(): void {
    this.feedbackService.getFeedbacks().subscribe(
      data => {
        this.feedbacks = data;
      },
      err => {
        console.error('Error obteniendo feedbacks:', err);
        this.error = 'Error en el servidor al obtener feedbacks.';
      }
    );
  }

  createFeedback(): void {
    this.feedbackService.createFeedback(this.newFeedback).subscribe(
      data => {
        this.feedbacks.push(data);
        this.newFeedback = {};
        this.router.navigate(['feedback']); // Navegar a la lista de ubicaciones después de crear
      },
      err => {
        console.error('Error creando feedbacks:', err);
        this.error = 'Error en el servidor al crear feedbacks.';
      }
    );
  }

  editFeedback(feedback: any): void {
    this.editingFeedbacks = { ...feedback };
  }

  updateFeedback(): void {
    this.feedbackService.updateFeedback(this.editingFeedbacks.fb_id, this.editingFeedbacks).subscribe(
      data => {
        const index = this.feedbacks.findIndex(u => u.fb_id === this.editingFeedbacks.fb_id);
        if (index !== -1) {
          this.feedbacks[index] = data;
        }
        this.editingFeedbacks = null;
        this.router.navigate(['feedback']); // Navegar a la lista de ubicaciones después de actualizar
      },
      err => {
        console.error('Error editando ubicación:', err);
        this.error = 'Error en el servidor al editar ubicación.';
      }
    );
  }

  deleteFeedback(fb_id: number): void {
    if (fb_id) {
      this.feedbackService.deleteFeedback(fb_id).subscribe(
        data => {
          this.feedbacks = this.feedbacks.filter(u => u.fb_id !== fb_id);
          this.router.navigate(['feedback']); // Navegar a la lista de feedback después de eliminar
        },
        err => {
          console.error('Error eliminando feedback:', err);
          this.error = 'Error en el servidor al eliminar feedback.';
        }
      );
    } else {
      console.error('ID de ubicación no válido:', fb_id);
    }
  }

  cancelEdit(): void {
    this.editingFeedbacks = null;
  }

}
<!DOCTYPE html>
<html lang="es">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Feedback Formulario</title>
    <link rel="stylesheet" href="feedback.component.css">
</head>
<body>
    <div class="page-container">
        <div class="background">
            <div class="shape"></div>
            <div class="shape"></div>
        </div>
        <div class="fb-container">
            <h2>Envianos cualquier sugerencia o recomendación</h2>
            <div *ngIf="error" class="error-message">{{ error }}</div>
            <div class="feedback-list-container">
                <ul>
                    <li *ngFor="let feedback of feedbacks" class="row">
                        <div class="col">
                            <h3>{{ feedback.titulo }}</h3>
                            <p class="text-content">{{ feedback.texto }}</p> <!-- Aplicar clase aquí -->
                        </div>
                        <div class="col">
                            <button (click)="editFeedback(feedback)" class="btn btn-warning">Editar</button>
                            <button (click)="deleteFeedback(feedback.fb_id)" class="btn btn-danger">Eliminar</button>
                        </div>
                    </li>
                </ul>
            </div>

            <div *ngIf="!editingFeedbacks">
                <h3>Proporcionanos tu retroalimentación</h3>
                <form (ngSubmit)="createFeedback()" class="row">
                    <div class="col-50">
                        <label for="titulo">Titulo:</label>
                        <input type="text" id="titulo" [(ngModel)]="newFeedback.titulo" name="titulo" required>
                    </div>
                    <div class="col-50">
                        <label for="texto">Idea:</label>
                        <textarea id="texto" [(ngModel)]="newFeedback.texto" name="texto" required></textarea>
                    </div>
                    <div class="col">
                        <button type="submit" class="btn btn-success">Mandar feedback</button>
                    </div>
                </form>
            </div>

            <div *ngIf="editingFeedbacks">
                <h3>Editar Feedback</h3>
                <form (ngSubmit)="updateFeedback()" class="row">
                    <div class="col-50">
                        <label for="editTitulo">Actualizar titulo:</label>
                        <input type="text" id="editTitulo" [(ngModel)]="editingFeedbacks.titulo" name="editTitulo" required>
                    </div>
                    <div class="col-50">
                        <label for="editTexto">Actualizar idea:</label>
                        <textarea id="editTexto" [(ngModel)]="editingFeedbacks.texto" name="editTexto" required></textarea>
                    </div>
                    <div class="col">
                        <button type="submit" class="btn btn-success">Guardar Cambios</button>
                        <button type="button" (click)="cancelEdit()" class="btn btn-secondary">Cancelar</button>
                    </div>
                </form>
            </div>
        </div>
    </div>
</body>
</html>
