import { Component, OnInit } from '@angular/core';
import { UbicacionService } from 'src/app/services/ubicacion.service';
import { Router } from '@angular/router';

@Component({
  selector: 'app-ubicacion',
  templateUrl: './ubicacion.component.html',
  styleUrls: ['./ubicacion.component.css']
})
export class UbicacionComponent implements OnInit {
  ubicaciones: any[] = [];
  newUbicacion: any = {};
  editingUbicacion: any = null;
  error = '';

  constructor(private ubicacionService: UbicacionService, private router: Router) { }

  ngOnInit(): void {
    this.loadUbicaciones();
  }

  loadUbicaciones(): void {
    this.ubicacionService.getUbicaciones().subscribe(
      data => {
        this.ubicaciones = data;
      },
      err => {
        console.error('Error obteniendo ubicaciones:', err);
        this.error = 'Error en el servidor al obtener ubicaciones.';
      }
    );
  }

  createUbicacion(): void {
    this.ubicacionService.createUbicacion(this.newUbicacion).subscribe(
      data => {
        this.ubicaciones.push(data);
        this.newUbicacion = {};
        this.editingUbicacion = null;
      },
      err => {
        console.error('Error al añadir la ubicación:', err);
        this.error = 'Error en el servidor al añadir la ubicación.';
      }
    );
  }

  editUbicacion(ubicacion: any): void {
    this.editingUbicacion = { ...ubicacion };
  }

  updateUbicacion(id_gimnasio: number): void {
    this.ubicacionService.updateUbicacion(id_gimnasio, this.editingUbicacion).subscribe(
      data => {
        const index = this.ubicaciones.findIndex(u => u.id_gimnasio === id_gimnasio);
        if (index !== -1) {
          this.ubicaciones[index] = data;
        }
        this.editingUbicacion = null;
      },
      err => {
        console.error('Error actualizando ubicación:', err);
        this.error = 'Error en el servidor al actualizar ubicación.';
      }
    );
  }

  deleteUbicacion(id_gimnasio: number): void {
    if (id_gimnasio) {
      this.ubicacionService.deleteUbicacion(id_gimnasio).subscribe(
        data => {
          this.ubicaciones = this.ubicaciones.filter(u => u.id_gimnasio !== id_gimnasio);
        },
        err => {
          console.error('Error eliminando ubicación:', err);
          this.error = 'Error en el servidor al eliminar ubicación.';
        }
      );
    } else {
      console.error('ID de ubicación no válido:', id_gimnasio);
    }
  }

  cancelEdit(): void {
    this.editingUbicacion = null;
  }
}
<div class="container-xl gimnasios-container">
  <h2 id="titulo">Lista de Gimnasios</h2>
  <div *ngIf="error" class="error-message">{{ error }}</div>

  <table class="table">
    <thead>
      <tr>
        <th>Nombre del Gimnasio</th>
        <th>Ubicación</th>
        <th>Teléfono</th>
        <th>Imagen</th>
        <th>Acciones</th>
      </tr>
    </thead>
    <tbody>
      <tr *ngFor="let gimnasio of gimnasios; let i = index">
        <td>{{ gimnasio.nombre_gimnasio }}</td>
        <td>{{ gimnasio.ubicacion }}</td>
        <td>{{ gimnasio.telefono }}</td>
        <td>
          <img [src]="gimnasio.imagen_url" alt="{{ gimnasio.nombre_gimnasio }}" class="gimnasio-imagen">
        </td>
        <td>
          <button (click)="editGimnasio(gimnasio)" class="btn btn-warning">Editar</button>
          <button (click)="deleteGimnasio(gimnasio.id_gimnasio)" class="btn btn-danger">Eliminar</button>
        </td>
      </tr>
    </tbody>
  </table>

  <div *ngIf="!editingGimnasio">
    <h3>Crear Gimnasio</h3>
    <form (ngSubmit)="createGimnasio()" class="gimnasio-form">
      <div class="form-group">
        <label for="nombre_gimnasio">Nombre del Gimnasio:</label>
        <input type="text" id="nombre_gimnasio" [(ngModel)]="newGimnasio.nombre_gimnasio" name="nombre_gimnasio" class="form-control" required>
      </div>
      <div class="form-group">
        <label for="ubicacion">Ubicación:</label>
        <input type="text" id="ubicacion" [(ngModel)]="newGimnasio.ubicacion" name="ubicacion" class="form-control" required>
      </div>
      <div class="form-group">
        <label for="telefono">Teléfono:</label>
        <input type="text" id="telefono" [(ngModel)]="newGimnasio.telefono" name="telefono" class="form-control" required>
      </div>
      <div class="form-group">
        <label for="imagen_url">URL de la Imagen:</label>
        <input type="text" id="imagen_url" [(ngModel)]="newGimnasio.imagen_url" name="imagen_url" class="form-control" required>
      </div>
      <button type="submit" class="btn btn-success">Crear Gimnasio</button>
    </form>
  </div>

  <div *ngIf="editingGimnasio">
    <h3>Editar Gimnasio</h3>
    <form (ngSubmit)="updateGimnasio(editingGimnasio.id_gimnasio)" class="gimnasio-form">
      <div class="form-group">
        <label for="editNombreGimnasio">Nombre del Gimnasio:</label>
        <input type="text" id="editNombreGimnasio" [(ngModel)]="editingGimnasio.nombre_gimnasio" name="editNombreGimnasio" class="form-control" required>
      </div>
      <div class="form-group">
        <label for="editUbicacion">Ubicación:</label>
        <input type="text" id="editUbicacion" [(ngModel)]="editingGimnasio.ubicacion" name="editUbicacion" class="form-control" required>
      </div>
      <div class="form-group">
        <label for="editTelefono">Teléfono:</label>
        <input type="text" id="editTelefono" [(ngModel)]="editingGimnasio.telefono" name="editTelefono" class="form-control" required>
      </div>
      <div class="form-group">
        <label for="editImagenUrl">URL de la Imagen:</label>
        <input type="text" id="editImagenUrl" [(ngModel)]="editingGimnasio.imagen_url" name="editImagenUrl" class="form-control" required>
      </div>
      <button type="submit" class="btn btn-primary">Guardar Cambios</button>
      <button type="button" (click)="cancelEdit()" class="btn btn-secondary">Cancelar</button>
    </form>
  </div>
</div>
