import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Observable } from 'rxjs';

@Injectable({
  providedIn: 'root'
})
export class AuthService {

  private baseUrl = 'http://localhost:3000/auth'; // Base URL para el servicio de autenticación

  constructor(private http: HttpClient) { }

  login(credentials: { usuario: string; contraseña: string | number = ''; }): Observable<any> {
    return this.http.post(`${this.baseUrl}/login`, credentials); // URL actualizada
  }
}
