import { Component, OnInit } from '@angular/core';
import { RutinaService } from 'src/app/services/rutina.Service';
import { Router } from '@angular/router';

@Component({
  selector: 'app-rutina',
  templateUrl: './rutina.component.html',
  styleUrls: ['./rutina.component.css']
})
export class RutinaComponent implements OnInit { // Cambié ProductosComponent por RutinaComponent
  scrollPosition = 0;
  rutinas: any[] = [];
  newRutina: any = {};
  editingRutina: any = null;
  selectedFile: File | null = null; 
  error = '';
  filter: string = '';

  private apiUrl = 'http://localhost:3000/api/rutinas'; // Mantengo la URL por si es usada en algún otro lugar

  constructor(private rutinaService: RutinaService, private router: Router) { }

  ngOnInit(): void {
    this.loadRutinas();
  }

  loadRutinas(): void {
    this.rutinaService.getRutinas().subscribe(
      data => {
        this.rutinas = data;
      },
      err => {
        console.error('Error obteniendo rutinas:', err); // Cambié productos por rutinas
        this.error = 'Error en el servidor al obtener rutinas.'; // Cambié el mensaje
      }
    );
  }

  onFileChange(event: Event): void {
    const element = event.target as HTMLInputElement;
    if (element.files && element.files.length > 0) {
      this.newRutina.imagen_url = element.files[0]; // Cambié imagen_pro por imagen_url
    }
  }

  createRutina(): void {
    this.rutinaService.createRutina(this.newRutina).subscribe(
      data => {
        this.rutinas.push(data);
        this.newRutina = {};
        this.router.navigate(['rutina']);
      },
      err => {
        console.error('Error creando rutina:', err); // Cambié producto por rutina
        this.error = 'Error en el servidor al crear rutina.'; // Cambié el mensaje
      }
    );
  }

  editRutina(rutina: any): void {
    this.editingRutina = { ...rutina };
  }

  updateRutina(id_rutina: number): void { // Cambié upateRutina por updateRutina
    if (this.selectedFile && !this.editingRutina.imagen_url) { // Cambié imagen_pro por imagen_url
      this.rutinaService.uploadImagen(this.selectedFile).subscribe(
        response => {
          this.editingRutina.imagen_url = response.imageUrl; // Cambié imagen_pro por imagen_url
          this.saveUpdatedRutina(id_rutina);
        },
        error => {
          console.error('Error al cargar la imagen:', error);
        }
      );
    } else {
      this.saveUpdatedRutina(id_rutina);
    }
  }

  saveUpdatedRutina(id_rutina: number): void {
    this.rutinaService.updateRutina(id_rutina, this.editingRutina).subscribe(
      data => {
        const index = this.rutinas.findIndex(p => p.id_rutina === id_rutina);
        if (index !== -1) {
          this.rutinas[index] = data;
        }
        this.editingRutina = null;
      },
      err => {
        console.error('Error actualizando rutina:', err); // Cambié producto por rutina
        this.error = 'Error en el servidor al actualizar rutina.'; // Cambié el mensaje
      }
    );
  }

  deleteRutina(id: number): void {
    if (id) {
      this.rutinaService.deleteRutina(id).subscribe(
        data => {
          this.rutinas = this.rutinas.filter(p => p.id_rutina !== id);
          this.router.navigate(['rutina']);
        },
        err => {
          console.error('Error eliminando la rutina:', err); // Cambié producto por rutina
          this.error = 'Error en el servidor al eliminar rutina.'; // Cambié el mensaje
        }
      );
    } else {
      console.error('No se encontró la rutina:', id); // Cambié ejercicio por rutina
    }
  }
}
<div class="container-xl rutina-container">
  <h2>Lista de Rutinas</h2>
  <div *ngIf="error" class="alert alert-danger">{{ error }}</div>
  <input type="text" [(ngModel)]="filter" class="form-control mb-3" placeholder="Buscar rutinas...">

  <table class="table rutina-table">
    <thead class="thead-light">
      <tr>
        <th scope="col">Nombre</th>
        <th scope="col">Enlace</th>
        <th scope="col">Descripción</th>
        <th scope="col">Imagen</th>
        <th scope="col">Fecha de creación</th>
        <th scope="col">Edición</th>
      </tr>
    </thead>
    <tbody>
      <tr *ngFor="let rutina of rutinas | filter:filter:['nombre_rutina', 'enlace']">
        <td>{{ rutina.nombre_rutina }}</td>
        <td><a [href]="rutina.enlace" target="_blank">Ver Rutina</a></td>
        <td>{{ rutina.descripcion }}</td>
        <td class="img-cell">
          <img [src]="rutina.imagen_url" alt="{{ rutina.nombre_rutina }}" class="img-fluid">
        </td>

        <td>
          <button (click)="editRutina(rutina)" class="btn btn-warning btn-action edit">Editar</button>
          <button (click)="deleteRutina(rutina.id_rutina)" class="btn btn-danger btn-action delete">Eliminar</button>
        </td>
      </tr>
    </tbody>
  </table>

  <div *ngIf="!editingRutina">
    <h3>Crear Rutina</h3>
    <form (ngSubmit)="createRutina()" class="rutina-form">
      <div class="form-group">
        <label for="nombre_rutina">Nombre:</label>
        <input type="text" id="nombre_rutina" [(ngModel)]="newRutina.nombre_rutina" name="nombre_rutina" class="form-control" required>
      </div>
      <div class="form-group">
        <label for="enlace">Enlace:</label>
        <input type="url" id="enlace" [(ngModel)]="newRutina.enlace" name="enlace" class="form-control" required>
      </div>
      <div class="form-group">
        <label for="descripcion">Descripción:</label>
        <input type="text" id="descripcion" [(ngModel)]="newRutina.descripcion" name="descripcion" class="form-control" required>
      </div>
      <div class="form-group">
        <label for="imagen_url">URL de la Imagen:</label>
        <input type="text" id="imagen_url" [(ngModel)]="newRutina.imagen_url" name="imagen_url" class="form-control" required>
      </div>
      <div class="form-group">
        <label for="fecha_creacion">Fecha de Creación:</label>
        <input type="date" id="fecha_creacion" [(ngModel)]="newRutina.fecha_creacion" name="fecha_creacion" class="form-control" required>
      </div>
      <button type="submit" class="btn btn-success">Crear Rutina</button>
    </form>
  </div>

  <div *ngIf="editingRutina">
    <h3>Editar Rutina</h3>
    <form (ngSubmit)="updateRutina(editingRutina.id_rutina)" class="rutina-form">
      <div class="form-group">
        <label for="nombre_rutina-edit">Nombre:</label>
        <input type="text" id="nombre_rutina-edit" [(ngModel)]="editingRutina.nombre_rutina" name="nombre_rutina-edit" class="form-control" required>
      </div>
      <div class="form-group">
        <label for="enlace-edit">Enlace:</label>
        <input type="url" id="enlace-edit" [(ngModel)]="editingRutina.enlace" name="enlace-edit" class="form-control" required>
      </div>
      <div class="form-group">
        <label for="descripcion-edit">Descripción:</label>
        <input type="text" id="descripcion-edit" [(ngModel)]="editingRutina.descripcion" name="descripcion-edit" class="form-control" required>
      </div>
      <div class="form-group">
        <label for="imagen_url-edit">URL de la Imagen:</label>
        <input type="text" id="imagen_url-edit" [(ngModel)]="editingRutina.imagen_url" name="imagen_url-edit" class="form-control" required>
      </div>
      <div class="form-group">
        <label for="fecha_creacion-edit">Fecha de Creación:</label>
        <input type="date" id="fecha_creacion-edit" [(ngModel)]="editingRutina.fecha_creacion" name="fecha_creacion-edit" class="form-control" required>
      </div>
      <button type="submit" class="btn btn-primary">Guardar Cambios</button>
    </form>
  </div>
</div>
