import { Component, OnInit } from '@angular/core';
import { ProductoService } from 'src/app/services/producto.service';

@Component({
  selector: 'app-productos',
  templateUrl: './productos.component.html',
  styleUrls: ['./productos.component.css']
})
export class ProductosComponent implements OnInit {
  productos: any[] = [];
  newProducto: any = {};
  editingProducto: any = null;
  error = '';

  constructor(private productosService: ProductoService) { }

  ngOnInit(): void {
    this.loadProductos();
  }

  loadProductos(): void {
    this.productosService.getProductos().subscribe(
      data => {
        this.productos = data;
      },
      err => {
        console.error('Error obteniendo productos:', err);
        this.error = 'Error en el servidor al obtener productos.';
      }
    );
  }

  createProducto(): void {
    this.productosService.createProducto(this.newProducto).subscribe(
      data => {
        this.productos.push(data);
        this.newProducto = {};
      },
      err => {
        console.error('Error creando producto:', err);
        this.error = 'Error en el servidor al crear producto.';
      }
    );
  }

  editProducto(producto: any): void {
    this.editingProducto = { ...producto };
  }

  updateProducto(): void {
    this.productosService.updateProducto(this.editingProducto.id, this.editingProducto).subscribe(
      data => {
        const index = this.productos.findIndex(p => p.id === this.editingProducto.id);
        if (index !== -1) {
          this.productos[index] = data;
        }
        this.editingProducto = null;
      },
      err => {
        console.error('Error editando producto:', err);
        this.error = 'Error en el servidor al editar producto.';
      }
    );
  }

  deleteProducto(id: number): void {
    this.productoService.deleteProducto(id).subscribe(
      data => {
        this.productos = this.productos.filter(p => p.id !== id);
      },
      err => {
        console.error('Error eliminando producto:', err);
        this.error = 'Error en el servidor al eliminar producto.';
      }
    );
  }
}



<div class="productos-container">
  <h2>Lista de Productos</h2>
  <div *ngIf="error" class="error-message">{{ error }}</div>
  <ul>
    <li *ngFor="let producto of productos">
      <h3>{{ producto.nombre }}</h3>
      <p>Tipo: {{ producto.tipo }}</p>
      <p>Precio: {{ producto.precio }}</p>
      <p>Descripción: {{ producto.descripcion }}</p>
      <button (click)="editProducto(producto)">Editar</button>
      <button (click)="deleteProducto(producto.id)">Eliminar</button>
    </li>
  </ul>
  <button (click)="createProducto()">Crear Producto</button>
</div>
