import { Component, OnInit } from '@angular/core';
import { NgForm } from '@angular/forms';
import { Router } from '@angular/router';
import { AuthService } from 'src/app/services/auth.service';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent implements OnInit {
  usuario: string = '';
  contrasena: string = '';
  error: string = '';

  constructor(private authService: AuthService, private router: Router) { }

  ngOnInit(): void {
  }

  handleLogin(loginForm: NgForm) {
    if (loginForm.valid) {
      const credentials = {
        usuario: this.usuario,
        contrasena: this.contrasena
      };
      this.authService.login(credentials).subscribe(
        data => {
          if (data.auth) {
            localStorage.setItem('token', data.token);
            this.router.navigate(['/admin-dashboard']);
          } else {
            this.error = 'Usuario o contraseña incorrectos.';
          }
        },
        err => {
          console.error('Error logging in:', err);
          this.error = 'Error en el servidor, por favor intente nuevamente.';
        }
      );
    }
  }
}
<div>
  <h1>Login</h1>
  <form (ngSubmit)="handleLogin(loginForm)" #loginForm="ngForm">
    <input
      type="text"
      placeholder="Usuario"
      [(ngModel)]="usuario"
      name="user_name"
      required
      #user_name="ngModel"
    />
    <div *ngIf="user_name.invalid && user_name.touched" class="error">
      Usuario es requerido.
    </div>
    <input
      type="password"
      placeholder="Contraseña"
      [(ngModel)]="contrasena"
      name="user_password"
      required
      #user_password="ngModel"
    />
    <div *ngIf="user_password.invalid && user_password.touched" class="error">
      Contraseña es requerida.
    </div>
    <button type="submit" [disabled]="loginForm.invalid">Login</button>
  </form>
  <p *ngIf="error" style="color: red;">{{ error }}</p>
  <p>No tienes una cuenta? <a routerLink="/register">Regístrate aquí</a></p>
</div>
