import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Observable } from 'rxjs';

@Injectable({
  providedIn: 'root'
})
export class ApiService {
  private baseUrl = 'http://localhost:3000/';
  private productosUrl = `${this.baseUrl}/productos`;
  private ubicacionesUrl = `${this.baseUrl}/ubicaciones`;
 

  constructor(private http: HttpClient) { }

  // Productos
  getProductos(): Observable<any[]> {
    return this.http.get<any[]>(this.productosUrl);
  }

  getProductoById(id: number): Observable<any> {
    return this.http.get<any>(`${this.productosUrl}/${id}`);
  }

  createProducto(producto: any): Observable<any> {
    return this.http.post<any>(this.productosUrl, producto);
  }

  updateProducto(id: number, producto: any): Observable<any> {
    return this.http.patch<any>(`${this.productosUrl}/${id}`, producto);
  }

  deleteProducto(id: number): Observable<any> {
    return this.http.delete<any>(`${this.productosUrl}/${id}`);
  }

  // Ubicaciones
  getUbicaciones(): Observable<any[]> {
    return this.http.get<any[]>(this.ubicacionesUrl);
  }

  getUbicacionById(id: number): Observable<any> {
    return this.http.get<any>(`${this.ubicacionesUrl}/${id}`);
  }

  createUbicacion(ubicacion: any): Observable<any> {
    return this.http.post<any>(this.ubicacionesUrl, ubicacion);
  }

  updateUbicacion(id: number, ubicacion: any): Observable<any> {
    return this.http.patch<any>(`${this.ubicacionesUrl}/${id}`, ubicacion);
  }

  deleteUbicacion(id: number): Observable<any> {
    return this.http.delete<any>(`${this.ubicacionesUrl}/${id}`);
  }


}
