import { Component, OnInit } from '@angular/core';
import { NgForm } from '@angular/forms';
import { Router } from '@angular/router';
import { AuthService } from 'src/app/services/auth.service';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent implements OnIncredentials: any, contraseña?: string6: NgForm) {
throw new Error('Method not implemented.');
}
  usuario: string = '';
  contraseña: string = '';
  error: string = '';

  constructor(private authService: AuthService, private router: Router) { }

  ngOnInit(): void {
  }

  handleLogin(event: Event) {
    event.preventDefault();
    this.authService.login(this.usuario, this.contraseña).subscribe(
      data => {
        if (data.auth) {
          localStorage.setItem('token', data.token);
          this.router.navigate(['/admin-dashboard']);
        } else {
          this.error = 'Usuario o contraseña incorrectos.';
        }
      },
      err => {
        console.error('Error logging in:', err);
        this.error = 'Error en el servidor, por favor intente nuevamente.';
      }
    );
  }
}
