import { Component } from '@angular/core';

import emailjs, { type EmailJSResponseStatus } from '@emailjs/browser';



@Component({
  selector: 'app-correo',
  templateUrl: './correo.component.html',
  styleUrls: ['./correo.component.css']
})
export class CorreoComponent {
  public sendEmail(e: Event) {
    e.preventDefault();

    emailjs
      .sendForm('service_o0dju72', 'template_fw98ehe', e.target as HTMLFormElement, {
        publicKey: '3fssA4Flpz0Pbiqhu',
      })
      .then(
        () => {
          console.log('SUCCESS!');
        },
        (err) => {
          console.log('FAILED...', (err as EmailJSResponseStatus).text);
        },
      );
  }
}
<!DOCTYPE html>
<html lang="es">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Formulario de Contacto</title>
    <link rel="stylesheet" href="correo.component.css">
</head>
<body>
    <div class="page-container">
        <div class="contact-wrapper">
            <form class="contact-form" (submit)="sendEmail($event)">
                <h2>Contáctanos</h2>
                <label>Ingrese su Usuario</label>
                <input type="text" name="user_name" placeholder="Usuario">
                <label>Ingrese su correo</label>
                <input type="email" name="user_email" placeholder="Correo Electrónico">
                <input type="submit" value="Enviar">
                <a class="Menu-list-item" [routerLink]="'/login'">
                    <span class="text">Regresar</span>
                    <span class="Mask"><span><i class="bi bi-arrow-return-left"></i></span></span>
                </a>
            </form>
        </div>
    </div>
</body>
</html>
