    <link rel="stylesheet" href="feedback.component.css">
</head>
<body>
    <div id="container"></div>
    <script id="template">
        <div class="flip-card" on-click="toggle('flipCard')">{{ flipCard ? 'Reset' : 'Animate' }}</div>
        <div class="contact-wrapper">
            <div class="envelope {{ flipCard ? 'active' : '' }}">
                <div class="back paper"></div>
                <div class="content">
                    <div class="form-wrapper">
                        <form (ngSubmit)="createFeedback()">
                            <div class="top-wrapper">
                                <div class="input">
                                    <label for="titulo">Titulo:</label>
                                    <input type="text" id="titulo" [(ngModel)]="newFeedback.titulo" name="titulo" required>
                                </div>
                                <div class="input">
                                    <label for="texto">Idea:</label>
                                    <input type="text" id="texto" [(ngModel)]="newFeedback.texto" name="texto" required>
                                </div>
                            </div>
                            <div class="bottom-wrapper">
                                <div class="submit">
                                    <div class="submit-card" on-click="toggle('flipCard')">Mandar feedback</div>
                                </div>
                            </div>
                        </form>
                    </div>
                </div>
                <div class="front paper"></div>
            </div>
        </div>
    </script>
