import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = 'frontend';
}
<nav class="navbar">
  <a href="#inicio">Inicio</a>
  <a href="#componentes">Componentes</a>
  <a href="#adquisiciones">Adquisiciones</a>
  <a href="#sobre-nosotros">Sobre Nosotros</a>
  <a href="#contactenos">Contáctenos</a>
</nav>
<div class="highlight"></div>

<div class="container">
  <h1>Consulta y conoce más acerca de Arduino</h1>
  <h1>Arduino Knowledge</h1>
  <p>Desde precios, nombres hasta localizaciones de componentes muy conocidos hasta los más desconocidos</p>
  <button class="button">Descúbrelo</button>
</div>
