import { Component, OnInit } from '@angular/core';
import { UbicacionService } from 'src/app/services/ubicacion.service';
import { Router } from '@angular/router';

@Component({
  selector: 'app-ubicacion',
  templateUrl: './ubicacion.component.html',
  styleUrls: ['./ubicacion.component.css']
})
export class UbicacionComponent implements OnInit {
  scrollPosition = 0;
  ubicaciones: any[] = [];
  newUbicacion: any = {};
  editingUbicacion: any = null;
  error = '';

  constructor(private ubicacionService: UbicacionService, private router: Router) { }

  ngOnInit(): void {
    this.loadUbicaciones();
  }

  scrollRight(): void {
    this.scrollPosition += 200;  // Ajusta este valor según necesites
  }

  scrollLeft(): void {
    this.scrollPosition -= 200;  // Ajusta este valor según necesites
  }

  loadUbicaciones(): void {
    this.ubicacionService.getUbicaciones().subscribe(
      data => {
        this.ubicaciones = data;
      },
      err => {
        console.error('Error obteniendo ubicaciones:', err);
        this.error = 'Error en el servidor al obtener ubicaciones.';
      }
    );
  }

  createUbicacion(): void {
    this.ubicacionService.createUbicacion(this.newUbicacion).subscribe(
      data => {
        this.ubicaciones.push(data);
        this.newUbicacion = {};
        this.router.navigate(['ubicacion']);
      },
      err => {
        console.error('Error al añadir la ubicación:', err);
        this.error = 'Error en el servidor al añadir la ubicación.';
      }
    );
  }

  saveUbicacion(ubicacion: any): void {
    this.ubicacionService.createUbicacion(ubicacion).subscribe(
      data => {
        this.ubicaciones.push(data);
        this.newUbicacion = {};
        this.router.navigate(['ubicacion']);
      },
      err => {
        console.error('Error creando ubicación:', err);
        this.error = 'Error en el servidor al crear ubicación.';
      }
    );
  }

  editUbicacion(ubicacion: any): void {
    this.editingUbicacion = { ...ubicacion };
  }

  updateUbicacion(ubi_id: number): void {
    this.saveUpdatedUbicacion(ubi_id);
  }

  saveUpdatedUbicacion(ubi_id: number): void {
    this.ubicacionService.updateUbicacion(ubi_id, this.editingUbicacion).subscribe(
      data => {
        const index = this.ubicaciones.findIndex(u => u.ubi_id === this.editingUbicacion.ubi_id);
        if (index !== -1) {
          this.ubicaciones[index] = data;
        }
        this.editingUbicacion = null;
        this.router.navigate(['ubicacion']); // Navegar a la lista de ubicaciones después de actualizar
      },
      err => {
        console.error('Error actualizando ubicación:', err);
        this.error = 'Error en el servidor al actualizar ubicación.';
      }
    );
  }

  deleteUbicacion(ubi_id: number): void {
    if (ubi_id) {
      this.ubicacionService.deleteUbicacion(ubi_id).subscribe(
        data => {
          this.ubicaciones = this.ubicaciones.filter(u => u.ubi_id !== ubi_id);
          this.router.navigate(['ubicacion']); // Navegar a la lista de ubicaciones después de eliminar
        },
        err => {
          console.error('Error eliminando ubicación:', err);
          this.error = 'Error en el servidor al eliminar ubicación.';
        }
      );
    } else {
      console.error('ID de ubicación no válido:', ubi_id);
    }
  }
}
<div class="container-xl ubicaciones-container">
  <h2 id="titulo">Lista de Ubicaciones</h2>
  <div *ngIf="error" class="error-message">{{ error }}</div>
  <table>
    <tr>

    </tr>
    <tr *ngFor="let ubicacion of ubicaciones; let i = index">
      <td>
        <div class="thumb-wrapper">
          <div class="thumb-content">
            <h4>{{ ubicacion.nombre }}</h4>
            <p><strong>Dirección:</strong> {{ ubicacion.direccion }}</p>
            <div>
              <img [src]="ubicacion.imagen_ubi" alt="{{ ubicacion.nombre }}" class="ubicacion-imagen">
            </div>
            <div>
              <button (click)="editUbicacion(ubicacion)" class="btn btn-warning">Editar</button>
              <button (click)="deleteUbicacion(ubicacion.ubi_id)" class="btn btn-danger">Eliminar</button>
            </div>
          </div>
        </div>
      </td>
    </tr>
  </table>

  <h3>Crear Ubicación</h3>
  <form (ngSubmit)="createUbicacion()" enctype="multipart/form-data" class="ubicacion-form">
    <label for="nombre">Nombre:</label>
    <input type="text" id="nombre" [(ngModel)]="newUbicacion.nombre" name="nombre" class="form-control" required><br>

    <label for="direccion">Dirección:</label>
    <input type="text" id="direccion" [(ngModel)]="newUbicacion.direccion" name="direccion" class="form-control" required><br>

    <label for="imagen_ubi">URL de la Imagen:</label>
    <input type="text" id="imagen_ubi" [(ngModel)]="newUbicacion.imagen_ubi" name="imagen_ubi" class="form-control" required><br>

    <button type="submit" class="btn btn-success">Crear Ubicación</button>
  </form>

  <div *ngIf="editingUbicacion">
    <h3>Editar Ubicación</h3>
    <form (ngSubmit)="updateUbicacion(editingUbicacion.ubi_id)" enctype="multipart/form-data" class="ubicacion-form">
      <label for="editNombre">Nombre:</label>
      <input type="text" id="editNombre" [(ngModel)]="editingUbicacion.nombre" name="editNombre" class="form-control" required><br>

      <label for="editDireccion">Dirección:</label>
      <input type="text" id="editDireccion" [(ngModel)]="editingUbicacion.direccion" name="editDireccion" class="form-control" required><br>

      <label for="editImagenUbi">URL de la Imagen:</label>
      <input type="text" id="editImagenUbi" [(ngModel)]="editingUbicacion.imagen_ubi" name="editImagenUbi" class="form-control" required><br>

      <button type="submit" class="btn btn-primary">Guardar Cambios</button>
    </form>
  </div>
</div>
