import { Component } from '@angular/core';

@Component({
  selector: 'app-inicio',
  templateUrl: './inicio.component.html',
  styleUrls: ['./inicio.component.css']
})
export class InicioComponent {

}
<h1>
  Explora con nosotros los horizontes de Arduino
</h1>

<h2>Consulte la información que tenga en mente</h2>

<p>Unknown Blacksmith, la compañía que quiere darte una biblioteca de información desde la comodidad de tu casa</p>

<ul id="c">
  <li><img class="arduinos" src="../assets/img/arduino_general.jpg" alt="general"></li>
  <li><img class="arduinos" src="../assets/img/componentes.webp" alt="componentes"></li>
  <li><img class="arduinos" src="../assets/img/placa_2.png" alt="placa 2"></li>
  <li><img class="arduinos" src="../assets/img/placas_arduino.jpg" alt="placas arduino"></li>
  <li><img class="arduinos" src="../assets/img/sensores_ard.jpg" alt="sensores arduino"></li>
</ul>
