import { Component, OnInit } from '@angular/core';
import { NgForm } from '@angular/forms';
import { AuthService } from 'src/app/services/auth.service';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent implements OnInit {
  usuario: string = '';
  contrasena: string = '';
  error: string = '';

  constructor(private authService: AuthService) { }

  ngOnInit(): void { }

  handleLogin(loginForm: NgForm) {
    if (loginForm.valid) {
      const credentials = {
        usuario: this.usuario,
        contrasena: this.contrasena
      };
      this.authService.login(credentials).subscribe(
        data => {
          if (data.auth) {
            localStorage.setItem('token', data.token);
            // Acción exitosa, por ejemplo, mostrar un mensaje o redirigir a otra página
          } else {
            this.error = data.message || 'Usuario o contraseña incorrectos.';
          }
        },
        err => {
          console.error('Error logging in:', err);
          this.error = 'Error en el servidor, por favor intente nuevamente.';
        }
      );
    }
  }
}
<div class="login-container">
  <form (ngSubmit)="handleLogin()">
    <div>
      <label for="usuario">Usuario:</label>
      <input type="text" id="usuario" name="usuario" [(ngModel)]="usuario" required>
    </div>
    <div>
      <label for="contrasena">Contraseña:</label>
      <input type="password" id="contrasena" name="contrasena" [(ngModel)]="contrasena" required>
    </div>
    <button type="submit">Login</button>
    <div *ngIf="error" class="error-message">{{ error }}</div>
  </form>
</div>
