import { Component, OnInit } from '@angular/core';
import { ProductoService } from 'src/app/services/producto.service';
import { Router } from '@angular/router';

@Component({
  selector: 'app-productos',
  templateUrl: './productos.component.html',
  styleUrls: ['./productos.component.css']
})
export class ProductosComponent implements OnInit {
  scrollPosition = 0;
  productos: any[] = [];
  newProducto: any = {};
  editingProducto: any = null;
  selectedFile: File | null = null;
  error = '';

  constructor(private productoService: ProductoService, private router: Router) {}

  ngOnInit(): void {
    this.loadProductos();
  }

  scrollRight(): void {
    this.scrollPosition += 200;  // Ajusta este valor según necesites
  }

  scrollLeft(): void {
    this.scrollPosition -= 200;  // Ajusta este valor según necesites
  }

  loadProductos(): void {
    this.productoService.getProductos().subscribe(
      data => {
        this.productos = data;
      },
      err => {
        console.error('Error obteniendo productos:', err);
        this.error = 'Error en el servidor al obtener productos.';
      }
    );
  }

  onFileChange(event: Event): void {
    const element = event.target as HTMLInputElement;
    if (element.files && element.files.length > 0) {
      this.newProducto.imagen_pro = element.files[0];
    }
  }

  createProducto(): void {
    if (this.selectedFile) {
      this.productoService.uploadImagen(this.selectedFile).subscribe(
        response => {
          this.newProducto.imagen_pro = response.imageUrl;
          this.saveProducto(this.newProducto);
        },
        error => {
          console.error('Error al cargar la imagen:', error);
        }
      );
    } else {
      this.saveProducto(this.newProducto);
    }
  }

  saveProducto(producto: any): void {
    this.productoService.createProducto(producto).subscribe(
      data => {
        this.productos.push(data);
        this.newProducto = {};
        this.router.navigate(['productos']);
      },
      err => {
        console.error('Error creando producto:', err);
        this.error = 'Error en el servidor al crear producto.';
      }
    );
  }

  openCreateModal(): void {
    this.editingProducto = null;
    this.newProducto = {};
  }

  openEditModal(producto: any): void {
    this.editingProducto = { ...producto };
  }

  updateProducto(comp_id: number): void {
    if (this.selectedFile && !this.editingProducto.imagen_pro) {
      this.productoService.uploadImagen(this.selectedFile).subscribe(
        response => {
          this.editingProducto.imagen_pro = response.imageUrl;
          this.saveUpdatedProducto(comp_id);
        },
        error => {
          console.error('Error al cargar la imagen:', error);
        }
      );
    } else {
      this.saveUpdatedProducto(comp_id);
    }
  }

  saveUpdatedProducto(comp_id: number): void {
    this.productoService.updateProducto(comp_id, this.editingProducto).subscribe(
      data => {
        const index = this.productos.findIndex(p => p.comp_id === this.editingProducto.comp_id);
        if (index !== -1) {
          this.productos[index] = data;
        }
        this.editingProducto = null;
        this.router.navigate(['productos']);
      },
      err => {
        console.error('Error actualizando producto:', err);
        this.error = 'Error en el servidor al actualizar producto.';
      }
    );
  }

  deleteProducto(id: number): void {
    this.productoService.deleteProducto(id).subscribe(
      data => {
        this.productos = this.productos.filter(p => p.comp_id !== id);
        this.router.navigate(['productos']);
      },
      err => {
        console.error('Error eliminando producto:', err);
        this.error = 'Error en el servidor al eliminar producto.';
      }
    );
  }
}
<div class="container-xl productos-container">
  <h2>Lista de Productos</h2>
  <div *ngIf="error" class="error-message">{{ error }}</div>
  <div id="productCarousel" class="carousel slide" data-ride="carousel">
    <div class="carousel-inner">
      <div *ngFor="let producto of productos; let i = index" class="carousel-item" [class.active]="i === 0">
        <div class="thumb-wrapper">
          <span class="wish-icon"><i class="fa fa-heart-o"></i></span>
          <div class="thumb-content">
            <h4>{{ producto.nombre }}</h4>
            <p><strong>Tipo:</strong> {{ producto.tipo }}</p>
            <p><strong>Precio:</strong> {{ producto.precio }}</p>
            <p><strong>Descripción:</strong> {{ producto.descripcion }}</p>
            <div>
              <img [src]="producto.imagen_pro" alt="{{ producto.nombre }}" class="producto-imagen" style="max-width: 100%; height: auto;">
            </div>
            <div>
              <button (click)="openEditModal(producto)" class="btn btn-warning" data-toggle="modal" data-target="#productModal">Editar</button>
              <button (click)="deleteProducto(producto.comp_id)" class="btn btn-danger">Eliminar</button>
            </div>
          </div>
        </div>
      </div>
    </div>
  </div>

  <h3>Crear Producto</h3>
  <button (click)="openCreateModal()" class="btn btn-success" data-toggle="modal" data-target="#productModal">Crear Producto</button>

  <!-- Modal -->
  <div class="modal fade" id="productModal" tabindex="-1" role="dialog" aria-labelledby="productModalLabel" aria-hidden="true">
    <div class="modal-dialog" role="document">
      <div class="modal-content">
        <div class="modal-header">
          <h5 class="modal-title" id="productModalLabel">{{ editingProducto ? 'Editar Producto' : 'Crear Producto' }}</h5>
          <button type="button" class="close" data-dismiss="modal" aria-label="Close">
            <span aria-hidden="true">&times;</span>
          </button>
        </div>
        <div class="modal-body">
          <form (ngSubmit)="editingProducto ? updateProducto(editingProducto.comp_id) : createProducto()" class="product-form">
            <label for="nombre">Nombre:</label>
            <input type="text" id="nombre" [(ngModel)]="editingProducto ? editingProducto.nombre : newProducto.nombre" name="nombre" class="form-control"><br>

            <label for="tipo">Tipo:</label>
            <input type="text" id="tipo" [(ngModel)]="editingProducto ? editingProducto.tipo : newProducto.tipo" name="tipo" class="form-control"><br>

            <label for="precio">Precio:</label>
            <input type="number" id="precio" [(ngModel)]="editingProducto ? editingProducto.precio : newProducto.precio" name="precio" class="form-control"><br>

            <label for="descripcion">Descripción:</label>
            <input type="text" id="descripcion" [(ngModel)]="editingProducto ? editingProducto.descripcion : newProducto.descripcion" name="descripcion" class="form-control"><br>

            <label for="imagen_pro">URL de la Imagen:</label>
            <input type="text" id="imagen_pro" [(ngModel)]="editingProducto ? editingProducto.imagen_pro : newProducto.imagen_pro" name="imagen_pro" class="form-control"><br>

            <button type="submit" class="btn btn-primary">{{ editingProducto ? 'Actualizar Producto' : 'Crear Producto' }}</button>
          </form>
        </div>
      </div>
    </div>
  </div>
</div>
