import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-inicio',
  templateUrl: './inicio.component.html',
  styleUrls: ['./inicio.component.css']
})
export class InicioComponent implements OnInit {
  title = 'arduino-exploration';

  ngOnInit() {
    this.startCarousel();
  }

  startCarousel() {
    const timer = 4000;
    let i = 0;
    const items = document.querySelectorAll('.c > li');
    const max = items.length;

    const setPosition = (index: number, position: string, delay: string) => {
      if (items[index]) {
        items[index].classList.add('active');
        (items[index] as HTMLElement).style.left = position;
        (items[index] as HTMLElement).style.transitionDelay = delay;
      }
    };

    setPosition(i, '0', '0.25s');
    setPosition(i + 1, '25%', '0.5s');
    setPosition(i + 2, '50%', '0.75s');
    setPosition(i + 3, '75%', '1s');

    setInterval(() => {
      items.forEach(item => item.classList.remove('active'));

      if (i < max - 4) {
        i += 4;
      } else {
        i = 0;
      }

      setPosition(i, '0', '1.25s');
      setPosition(i + 1, '25%', '1.5s');
      setPosition(i + 2, '50%', '1.75s');
      setPosition(i + 3, '75%', '2s');
    }, timer);
  }
}
