import { Component, OnInit } from '@angular/core';
import { UbicacionService } from 'src/app/services/ubicacion.service';
import { HttpClient } from '@angular/common/http';
import { Router } from '@angular/router';

@Component({
  selector: 'app-ubicacion',
  templateUrl: './ubicacion.component.html',
  styleUrls: ['./ubicacion.component.css']
})
export class UbicacionComponent implements OnInit {
  ubicaciones: any[] = [];
  newUbicacion: any = {
    nombre: '',
    direccion: '',
    imagen_ubi: '',
    comp_id: 1, // Asignar un ID de componente por defecto
    admin_id: 1 // Asignar un ID de admin por defecto
  };
  editingUbicacion: any = null;
  error = '';

  constructor(
    private ubicacionService: UbicacionService,
    private http: HttpClient,
    private router: Router
  ) { }

  ngOnInit(): void {
    this.loadUbicaciones();
  }

  loadUbicaciones(): void {
    this.ubicacionService.getUbicaciones().subscribe(
      data => {
        this.ubicaciones = data;
      },
      err => {
        console.error('Error obteniendo ubicaciones:', err);
        this.error = 'Error en el servidor al obtener ubicaciones.';
      }
    );
  }

  createUbicacion(): void {
    this.ubicacionService.createUbicacion(this.newUbicacion).subscribe(
      data => {
        this.ubicaciones.push(data);
        this.newUbicacion = {
          nombre: '',
          direccion: '',
          imagen_ubi: '',
          comp_id: 1, // Restablecer el ID de componente por defecto
          admin_id: 1 // Restablecer el ID de admin por defecto
        };
        this.router.navigate(['/ubicaciones']); // Navegar a la lista de ubicaciones después de crear
      },
      err => {
        console.error('Error creando ubicación:', err);
        this.error = 'Error en el servidor al crear ubicación.';
      }
    );
  }

  editUbicacion(ubicacion: any): void {
    if (ubicacion && ubicacion.comp_id) {
      this.editingUbicacion = { ...ubicacion };
    } else {
      console.error('Ubicación no válida:', ubicacion);
    }
  }

  updateUbicacion(): void {
    this.ubicacionService.updateUbicacion(this.editingUbicacion.comp_id, this.editingUbicacion).subscribe(
      data => {
        const index = this.ubicaciones.findIndex(u => u.comp_id === this.editingUbicacion.comp_id);
        if (index !== -1) {
          this.ubicaciones[index] = data;
        }
        this.editingUbicacion = null;
        this.router.navigate(['/ubicaciones']); // Navegar a la lista de ubicaciones después de actualizar
      },
      err => {
        console.error('Error editando ubicación:', err);
        this.error = 'Error en el servidor al editar ubicación.';
      }
    );
  }

  deleteUbicacion(comp_id: number): void {
    if (comp_id) {
      this.ubicacionService.deleteUbicacion(comp_id).subscribe(
        data => {
          this.ubicaciones = this.ubicaciones.filter(u => u.comp_id !== comp_id);
          this.router.navigate(['/ubicaciones']); // Navegar a la lista de ubicaciones después de eliminar
        },
        err => {
          console.error('Error eliminando ubicación:', err);
          this.error = 'Error en el servidor al eliminar ubicación.';
        }
      );
    } else {
      console.error('ID de ubicación no válido:', comp_id);
    }
  }
}


<div class="productos-container">
  <h2>Lista de Productos</h2>
  <div *ngIf="error" class="error-message">{{ error }}</div>
  <ul>
    <li *ngFor="let producto of productos">
      <h3>{{ producto.nombre }}</h3>
      <p>Tipo: {{ producto.tipo }}</p>
      <p>Precio: {{ producto.precio }}</p>
      <p>Descripción: {{ producto.descripcion }}</p>
      <button (click)="editProducto(producto)">Editar</button>
      <button (click)="deleteProducto(producto.id)">Eliminar</button>
    </li>
  </ul>
  <h3>Crear Producto</h3>
  <form (ngSubmit)="createProducto()">
    <label for="nombre">Nombre:</label>
    <input type="text" id="nombre" [(ngModel)]="newProducto.nombre" name="nombre" required><br>

    <label for="tipo">Tipo:</label>
    <input type="text" id="tipo" [(ngModel)]="newProducto.tipo" name="tipo" required><br>

    <label for="precio">Precio:</label>
