import { Component, OnInit } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Router } from '@angular/router';
@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent implements OnInit {
  usuario: string = '';
  contrasena: string = '';
  error: string = '';

  private apiUrl = 'http://localhost:3000/auth/login';

  constructor(private http: HttpClient, private router:Router) { }

  ngOnInit(): void { }

  handleLogin() {
    const credentials = {
      usuario: this.usuario,
      contrasena: this.contrasena
    };

    this.http.post<any>(this.apiUrl, credentials).subscribe(res=> {
      console.log(res)
      if (res) {
        localStorage.setItem('usuario', this.usuario);
        console.log('Login exitoso');
        this.router.navigate(['inicio'])
        // Aquí puedes agregar cualquier lógica adicional después del login exitoso
      } else {
        this.error = res.message || 'Usuario o contraseña incorrectos.';
      }
    })}}

<div class="login-container">
  <form (ngSubmit)="handleLogin()">
    <div>
      <label for="usuario">Usuario:</label>
      <input type="text" id="usuario" name="usuario" [(ngModel)]="usuario" required>
    </div>
    <div>
      <label for="contrasena">Contraseña:</label>
      <input type="password" id="contrasena" name="contrasena" [(ngModel)]="contrasena" required>
    </div>
    <button type="submit">Login</button>
    <div *ngIf="error" class="error-message">{{ error }}</div>
  </form>
</div>
