import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { AuthService } from 'src/app/services/auth.service';
@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent {
  errorMessage: string | null = null;

  constructor(private authController: AuthContr, private router: Router) {}

  onSubmit(form: any) {
    if (form.valid) {
      this.authController.login(form.value).subscribe(
        response => {
          // Aquí puedes manejar la respuesta, como redirigir al usuario
          this.router.navigate(['/inicio']);
        },
        error => {
          this.errorMessage = error.error.message || 'Error en el inicio de sesión';
        }
      );
    } else {
      this.errorMessage = 'Por favor, completa el formulario correctamente.';
    }
  }
}
