import { Component, OnInit } from '@angular/core';
import { UbicacionService } from 'src/app/services/ubicacion.service';
import { HttpClient } from '@angular/common/http';
import { Router } from '@angular/router';

@Component({
  selector: 'app-ubicacion',
  templateUrl: './ubicacion.component.html',
  styleUrls: ['./ubicacion.component.css']
})
export class UbicacionComponent implements OnInit {
  ubicaciones: any[] = [];
  newUbicacion: any = {};
  editingUbicacion: any = null;
  error = '';

  private apiUrl = 'http://localhost:3000/api/ubicaciones';

  constructor(
    private ubicacionService: UbicacionService, private http: HttpClient, private router: Router ) { }

  ngOnInit(): void {
    this.loadUbicaciones();
  }

  loadUbicaciones(): void {
    this.ubicacionService.getUbicaciones().subscribe(
      data => {
        this.ubicaciones = data;
      },
      err => {
        console.error('Error obteniendo ubicaciones:', err);
        this.error = 'Error en el servidor al obtener ubicaciones.';
      }
    );
  }
