import { Component, OnInit } from '@angular/core';
import { RutinaService } from 'src/app/services/rutina.Service';
import { Router } from '@angular/router';

@Component({
  selector: 'app-rutina',
  templateUrl: './rutina.component.html',
  styleUrls: ['./rutina.component.css']
})
export class RutinaComponent implements OnInit { // Cambié ProductosComponent por RutinaComponent
  scrollPosition = 0;
  rutinas: any[] = [];
  newRutina: any = {};
  editingRutina: any = null;
  selectedFile: File | null = null; 
  error = '';
  filter: string = '';

  private apiUrl = 'http://localhost:3000/api/rutina'; // Mantengo la URL por si es usada en algún otro lugar

  constructor(private rutinaService: RutinaService, private router: Router) { }

  ngOnInit(): void {
    this.loadRutinas();
  }

  loadRutinas(): void {
    this.rutinaService.getRutinas().subscribe(
      data => {
        this.rutinas = data;
      },
      err => {
        console.error('Error obteniendo rutinas:', err); // Cambié productos por rutinas
        this.error = 'Error en el servidor al obtener rutinas.'; // Cambié el mensaje
      }
    );
  }

  onFileChange(event: Event): void {
    const element = event.target as HTMLInputElement;
    if (element.files && element.files.length > 0) {
      this.newRutina.imagen_url = element.files[0]; // Cambié imagen_pro por imagen_url
    }
  }

  createRutina(): void {
    this.rutinaService.createRutina(this.newRutina).subscribe(
      data => {
        this.rutinas.push(data);
        this.newRutina = {};
        this.router.navigate(['rutina']);
      },
      err => {
        console.error('Error creando rutina:', err); // Cambié producto por rutina
        this.error = 'Error en el servidor al crear rutina.'; // Cambié el mensaje
      }
    );
  }

  editRutina(rutina: any): void {
    this.editingRutina = { ...rutina };
  }

  updateRutina(id_rutina: number): void { // Cambié upateRutina por updateRutina
    if (this.selectedFile && !this.editingRutina.imagen_url) { // Cambié imagen_pro por imagen_url
      this.rutinaService.uploadImagen(this.selectedFile).subscribe(
        response => {
          this.editingRutina.imagen_url = response.imageUrl; // Cambié imagen_pro por imagen_url
          this.saveUpdatedRutina(id_rutina);
        },
        error => {
          console.error('Error al cargar la imagen:', error);
        }
      );
    } else {
      this.saveUpdatedRutina(id_rutina);
    }
  }

  saveUpdatedRutina(id_rutina: number): void {
    this.rutinaService.updateRutina(id_rutina, this.editingRutina).subscribe(
      data => {
        const index = this.rutinas.findIndex(p => p.id_rutina === id_rutina);
        if (index !== -1) {
          this.rutinas[index] = data;
        }
        this.editingRutina = null;
      },
      err => {
        console.error('Error actualizando rutina:', err); // Cambié producto por rutina
        this.error = 'Error en el servidor al actualizar rutina.'; // Cambié el mensaje
      }
    );
  }

  deleteRutina(id: number): void {
    if (id) {
      this.rutinaService.deleteRutina(id).subscribe(
        data => {
          this.rutinas = this.rutinas.filter(p => p.id_rutina !== id);
          this.router.navigate(['rutina']);
        },
        err => {
          console.error('Error eliminando la rutina:', err); // Cambié producto por rutina
          this.error = 'Error en el servidor al eliminar rutina.'; // Cambié el mensaje
        }
      );
    } else {
      console.error('No se encontró la rutina:', id); // Cambié ejercicio por rutina
    }
  }
}
<div class="container-xl productos-container">
  <h2>Lista de Productos</h2>
  <div *ngIf="error" class="alert alert-danger">{{ error }}</div>
  <input type="text" [(ngModel)]="filter" class="form-control mb-3" placeholder="Buscar productos...">

  <table class="table productos-table">
    <thead class="thead-light">
      <tr>
        <th scope="col">Nombre</th>
        <th scope="col">Tipo</th>
        <th scope="col">Precio</th>
        <th scope="col">Descripción</th>
        <th scope="col">Imagen</th>
        <th scope="col">Edición</th>
      </tr>
    </thead>
    <tbody>
      <tr *ngFor="let producto of productos | filter:filter:['nombre', 'tipo']">
        <td>{{ producto.nombre }}</td>
        <td>{{ producto.tipo }}</td>
        <td>{{ producto.precio | currency }}</td>
        <td>{{ producto.descripcion }}</td>
        <td class="img-cell">
          <img [src]="producto.imagen_pro" alt="{{ producto.nombre }}" class="img-fluid">
        </td>
        <td>
          <button (click)="editRutina(rutinas)" class="btn btn-warning btn-action edit">Editar</button>
          <button (click)="deleteRutina(rutinas.id_rutina)" class="btn btn-danger btn-action delete">Eliminar</button>
        </td>
      </tr>
    </tbody>
  </table>

  <div *ngIf="!editingProducto">
    <h3>Crear Producto</h3>
    <form (ngSubmit)="createRutina()" class="product-form">
      <div class="form-group">
        <label for="nombre">Nombre:</label>
        <input type="text" id="nombre" [(ngModel)]="newProducto.nombre" name="nombre" class="form-control" required>
      </div>
      <div class="form-group">
        <label for="tipo">Tipo:</label>
        <input type="text" id="tipo" [(ngModel)]="newProducto.tipo" name="tipo" class="form-control" required>
      </div>
      <div class="form-group">
        <label for="precio">Precio:</label>
        <input type="number" id="precio" [(ngModel)]="newProducto.precio" name="precio" class="form-control" required>
      </div>
      <div class="form-group">
        <label for="descripcion">Descripción:</label>
        <input type="text" id="descripcion" [(ngModel)]="newProducto.descripcion" name="descripcion" class="form-control" required>
      </div>
      <div class="form-group">
        <label for="imagen_pro">URL de la Imagen:</label>
        <input type="text" id="imagen_pro" [(ngModel)]="newProducto.imagen_pro" name="imagen_pro" class="form-control" required>
      </div>
      <button type="submit" class="btn btn-success">Crear Producto</button>
    </form>
  </div>

  <div *ngIf="editingProducto">
    <h3>Editar Producto</h3>
    <form (ngSubmit)="updateProducto(editingProducto.comp_id)" class="product-form">
      <div class="form-group">
        <label for="nombre-edit">Nombre:</label>
        <input type="text" id="nombre-edit" [(ngModel)]="editingProducto.nombre" name="nombre-edit" class="form-control" required>
      </div>
      <div class="form-group">
        <label for="tipo-edit">Tipo:</label>
        <input type="text" id="tipo-edit" [(ngModel)]="editingProducto.tipo" name="tipo-edit" class="form-control" required>
      </div>
      <div class="form-group">
        <label for="precio-edit">Precio:</label>
        <input type="number" id="precio-edit" [(ngModel)]="editingProducto.precio" name="precio-edit" class="form-control" required>
      </div>
      <div class="form-group">
        <label for="descripcion-edit">Descripción:</label>
        <input type="text" id="descripcion-edit" [(ngModel)]="editingProducto.descripcion" name="descripcion-edit" class="form-control" required>
      </div>
      <div class="form-group">
        <label for="imagen_pro-edit">URL de la Imagen:</label>
        <input type="text" id="imagen_pro-edit" [(ngModel)]="editingProducto.imagen_pro" name="imagen_pro-edit" class="form-control" required>
      </div>
      <button type="submit" class="btn btn-primary">Guardar Cambios</button>
    </form>
  </div>
</div>
