import { Component, OnInit } from '@angular/core';
import Swiper from 'swiper';
import 'swiper/swiper-bundle.css';

@Component({
  selector: 'app-inicio',
  templateUrl: './inicio.component.html',
  styleUrls: ['./inicio.component.css']
})
export class InicioComponent implements OnInit {
  title = 'arduino-exploration';

  ngOnInit() {
    const swiper = new Swiper('.swiper-container', {
      slidesPerView: 4,
      spaceBetween: 30,
      loop: true,
      pagination: {
        el: '.swiper-pagination',
        clickable: true,
      },
      navigation: {
        nextEl: '.swiper-button-next',
        prevEl: '.swiper-button-prev',
      },
      autoplay: {
        delay: 4000,
        disableOnInteraction: false,
      },
    });
  }
}
<h1>
  Explora con nosotros los horizontes de Arduino
</h1>

<h2>Consulte la información que tenga en mente</h2>

<p>Unknown Blacksmith, la compañía que quiere darte una biblioteca de información desde la comodidad de tu casa</p>

<div class="swiper-container">
  <div class="swiper-wrapper">
    <div class="swiper-slide"><img class="arduinos" src="../assets/img/arduino_general.jpg" alt="general"></div>
    <div class="swiper-slide"><img class="arduinos" src="../assets/img/componentes.webp" alt="componentes"></div>
    <div class="swiper-slide"><img class="arduinos" src="../assets/img/placa_2.png" alt="placa 2"></div>
    <div class="swiper-slide"><img class="arduinos" src="../assets/img/placas_arduino.jpg" alt="placas arduino"></div>
    <div class="swiper-slide"><img class="arduinos" src="../assets/img/sensores_ard.jpg" alt="sensores arduino"></div>
  </div>
  <!-- Add Pagination -->
  <div class="swiper-pagination"></div>
  <!-- Add Navigation -->
  <div class="swiper-button-next"></div>
  <div class="swiper-button-prev"></div>
</div>
