import { Component, OnInit } from '@angular/core';
import { UbicacionService } from 'src/app/services/ubicacion.service';

@Component({
  selector: 'app-ubicacion',
  templateUrl: './ubicacion.component.html',
  styleUrls: ['./ubicacion.component.css']
})
export class UbicacionComponent implements OnInit {
  ubicacion: any[] = [];
  error = '';

  constructor(private ubicacionService: UbicacionService) { }

  ngOnInit(): void {
    this.ubicacionService.getUbicaciones().subscribe(
      data => {
        this.ubicacion = data;
        console.log(this.ubicacion);
      },
      err => {
        console.error('Error obteniendo ubicaciones:', err);
        this.error = 'Error en el servidor al obtener ubicaciones.';
      }
    );
  }
}



<h2>Lista de Ubicaciones</h2>
<div *ngFor="let ubicacion of ubicacion">
  <h3>{{ ubicacion.nombre }}</h3>
  <p>Dirección: {{ ubicacion.direccion }}</p>

  <img [src]="ubicacion.imagen_ubi" alt="{{ ubicacion.nombre }}" style="width: 200px;">
</div>
