import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Observable } from 'rxjs';
import { tap } from 'rxjs/operators'; // Importa el operador tap

@Injectable({
  providedIn: 'root'
})
export class AuthService {
  private apiUrl = 'http://localhost:3000/auth'; // URL del backend
  private currentUser: { admin_id: number, usuario: string } | null = null;

  constructor(private http: HttpClient) { }

  login(credentials: { usuario: string, contrasena: string }): Observable<any> {
    return this.http.post<any>(`${this.apiUrl}/login`, credentials).pipe(
      tap(response => {
        if (response && response.admin_id) {
          this.setCurrentUser({ admin_id: response.admin_id, usuario: response.usuario });
        }
      })
    );
  }

  setCurrentUser(user: { admin_id: number, usuario: string }): void {
    this.currentUser = user;
    localStorage.setItem('currentUser', JSON.stringify(user)); // Guarda el usuario en localStorage
  }

  getCurrentUser(): { admin_id: number, usuario: string } | null {
    const user = localStorage.getItem('currentUser');
    return user ? JSON.parse(user) : null;
  }

  logout(): void {
    this.currentUser = null;
    localStorage.removeItem('currentUser');
  }
}
