import { Component, OnInit } from '@angular/core';
import { ProductoService } from 'src/app/services/producto.service';
import { Router } from '@angular/router';

@Component({
  selector: 'app-productos',
  templateUrl: './productos.component.html',
  styleUrls: ['./productos.component.css']
})
export class ProductosComponent implements OnInit {
  productos: any[] = [];
  newProducto: any = {};
  editingProducto: any = null;
  error = '';

  private apiUrl = 'http://localhost:3000/api/productos';

  constructor(private productoService: ProductoService, private router: Router) { }

  ngOnInit(): void {
    this.loadProductos();
  }

  loadProductos(): void {
    this.productoService.getProductos().subscribe(
      data => {
        this.productos = data;
      },
      err => {
        console.error('Error obteniendo productos:', err);
        this.error = 'Error en el servidor al obtener productos.';
      }
    );
  }

  onFileSelected(event: Event): void {
    const file = (event.target as HTMLInputElement).files[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = () => {
        this.newProducto.imagen_pro = reader.result as string;
      };
      reader.readAsDataURL(file);
    }
  }

  onEditFileSelected(event: Event): void {
    const file = (event.target as HTMLInputElement).files[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = () => {
        this.editingProducto.imagen_pro = reader.result as string;
      };
      reader.readAsDataURL(file);
    }
  }

  createProducto(): void {
    this.productoService.createProducto(this.newProducto).subscribe(
      data => {
        this.productos.push(data);
        this.newProducto = {};
        this.router.navigate(['productos']); // Navegar a la lista de productos después de crear
      },
      err => {
        console.error('Error creando producto:', err);
        this.error = 'Error en el servidor al crear producto.';
      }
    );
  }

  editProducto(producto: any): void {
    this.editingProducto = { ...producto };
  }

  updateProducto(): void {
    this.productoService.updateProducto(this.editingProducto.comp_id, this.editingProducto).subscribe(
      data => {
        const index = this.productos.findIndex(p => p.comp_id === this.editingProducto.comp_id);
        if (index !== -1) {
          this.productos[index] = data;
        }
        this.editingProducto = null;
        this.loadProductos();
      }, 
      err => {
        console.error('Error editando producto:', err);
        this.error = 'Error en el servidor al editar producto.';
      }
    );
  }

  deleteProducto(id: number): void {
    if (id) {
      this.productoService.deleteProducto(id).subscribe(
        data => {
          this.productos = this.productos.filter(p => p.comp_id !== id);
          this.router.navigate(['productos']); // Navegar a la lista de productos después de eliminar
        },
        err => {
          console.error('Error eliminando producto:', err);
          this.error = 'Error en el servidor al eliminar producto.';
        }
      );
    } else {
      console.error('No se encontro el producto:', id);
    }
  }
}
<div class="productos-container">
  <h2>Lista de Productos</h2>
  <div *ngIf="error" class="error-message">{{ error }}</div>
  <ul>
    <li *ngFor="let producto of productos">
      <h3>{{ producto.nombre }}</h3>
      <p>Tipo: {{ producto.tipo }}</p>
      <p>Precio: {{ producto.precio }}</p>
      <p>Descripción: {{ producto.descripcion }}</p>
      <img [src]="producto.imagen_pro" alt="Imagen del producto">
      <button (click)="editProducto(producto)">Editar</button>
      <button (click)="deleteProducto(producto.comp_id)">Eliminar</button>
    </li>
  </ul>
  <h3>Crear Producto</h3>
  <form (ngSubmit)="createProducto()">
    <label for="nombre">Nombre:</label>
    <input type="text" id="nombre" [(ngModel)]="newProducto.nombre" name="nombre"><br>

    <label for="tipo">Tipo:</label>
    <input type="text" id="tipo" [(ngModel)]="newProducto.tipo" name="tipo"><br>

    <label for="precio">Precio:</label>
    <input type="number" id="precio" [(ngModel)]="newProducto.precio" name="precio"><br>

    <label for="descripcion">Descripción:</label>
    <input type="text" id="descripcion" [(ngModel)]="newProducto.descripcion" name="descripcion"><br>

    <label for="imagen_pro">Imagen:</label>
    <input type="file" id="imagen_pro" (change)="onFileSelected($event)"><br>

    <button type="submit">Crear Producto</button>
  </form>

  <div *ngIf="editingProducto">
    <h3>Editar Producto</h3>
    <form (ngSubmit)="updateProducto()">
      <label for="editNombre">Nombre:</label>
      <input type="text" id="editNombre" [(ngModel)]="editingProducto.nombre" name="editNombre"><br>

      <label for="editTipo">Tipo:</label>
      <input type="text" id="editTipo" [(ngModel)]="editingProducto.tipo" name="editTipo"><br>

      <label for="editPrecio">Precio:</label>
      <input type="number" id="editPrecio" [(ngModel)]="editingProducto.precio" name="editPrecio"><br>

      <label for="editDescripcion">Descripción:</label>
      <input type="text" id="editDescripcion" [(ngModel)]="editingProducto.descripcion" name="editDescripcion"><br>

      <label for="editImagen_pro">Imagen:</label>
      <input type="file" id="editImagen_pro" (change)="onEditFileSelected($event)"><br>

      <button type="submit">Guardar Cambios</button>
    </form>
  </div>
</div>
