import { Component } from '@angular/core';

@Component({
  selector: 'app-contacto',
  templateUrl: './contacto.component.html',
  styleUrls: ['./contacto.component.css']
})
export class ContactoComponent {

  copiarAlPortapapeles(texto: string): void {
    // Verificar si la API del portapapeles está disponible
    if (navigator.clipboard) {
      navigator.clipboard.writeText(texto).then(() => {
        alert('¡Correo copiado al portapapeles!');
      }).catch(err => {
        console.error('Error al copiar al portapapeles:', err);
      });
    } else {
      console.error('El acceso al portapapeles no está disponible.');
    }
  }
}
<div class="page-container">
    <div class="background">
        <div class="shape"></div>
        <div class="shape"></div>
    </div>
    <div class="contact-container">
        <h1>Atención al cliente</h1>
        <h2>Comuníquese con nosotros</h2>
        <p>Tel. (52+) 449 118 4056</p>
        <p>
            Correo de contacto: 
            <span></span>
            <button (click)="copiarAlPortapapeles('velasco12bigchis@gmail.com')" class="copiar">
                velasco12bigchis@
            </button>
        </p>
        <p>
            Redes sociales: 
            <a href="https://www.facebook.com/tuPerfil" target="_blank">Facebook</a>
        </p>
    </div>
</div>
